import React, { useState } from 'react'
import HTMLReactParser from 'html-react-parser'
import { useParams } from 'react-router-dom'
import millify from 'millify'
import { Col, Row, Typography, Select } from 'antd'
import { MoneyCollectOutlined, DollarCircleOutlined, FundOutlined, ExclamationCircleOutlined, StopOutlined, TrophyOutlined, CheckOutlined, NumberOutlined, ThunderboltOutlined } from '@ant-design/icons';

import { useGetCryptoDetailsQuery } from '../services/cryptoApi'

const { Title, Text } = Typography
const { Option } = Select

const CryptoDetails = () => {
    const { coinId } = useParams();
    const [ timePeriod, setTimePeriod ] = useState('7d')
    const { data, isFetching } = useGetCryptoDetailsQuery(coinId)
    const cryptoDts = data?.data?.coin;

    const time = ['3h', '24h', '7d', '30d', '1y', '3m', '3y', '5y'];

    const stats = [
      { title: 'Price to USD', value: `$ ${cryptoDts.price && millify(cryptoDts.price)}`, icon: <DollarCircleOutlined /> },
      { title: 'Rank', value: cryptoDts.rank, icon: <NumberOutlined /> },
      { title: '24h Volume', value: `$ ${cryptoDts.volume && millify(cryptoDts.volume)}`, icon: <ThunderboltOutlined /> },
      { title: 'Market Cap', value: `$ ${cryptoDts.marketCap && millify(cryptoDts.marketCap)}`, icon: <DollarCircleOutlined /> },
      { title: 'All-time-high(daily avg.)', value: `$ ${millify(cryptoDts.allTimeHigh.price)}`, icon: <TrophyOutlined /> },
    ];

    const genericStats = [
      { title: 'Number Of Markets', value: cryptoDts.numberOfMarkets, icon: <FundOutlined /> },
      { title: 'Number Of Exchanges', value: cryptoDts.numberOfExchanges, icon: <MoneyCollectOutlined /> },
      { title: 'Aprroved Supply', value: cryptoDts.approvedSupply ? <CheckOutlined /> : <StopOutlined />, icon: <ExclamationCircleOutlined /> },
      { title: 'Total Supply', value: `$ ${millify(cryptoDts.totalSupply)}`, icon: <ExclamationCircleOutlined /> },
      { title: 'Circulating Supply', value: `$ ${millify(cryptoDts.circulatingSupply)}`, icon: <ExclamationCircleOutlined /> },
    ];


    return (
        <Col className="coin-detail-container">
            <Col className="coin-heading-container">
                <Title level={2} className="coin-name">{cryptoDetails.name} ({cryptoDetails.slug})</Title>
            </Col>
        </Col>
    )
}

export default CryptoDetails
